            fontSize: 15,
            color:'red'
            };
            function makeOption(type, symbol) {
            return {
                legend: {
                data: ['2022', '2023'],
                right:0,
                textStyle: {
                    color: 'rgba(255,255,255,0.8)' // 设置图例的字体颜色为红色
