                grid: {
                containLabel: true,
                top: '10%',
                left: 5,
                bottom : '10%'
                },
                yAxis: {
                data: ['bad', 'low', 'mid', 'high'],
                inverse: true,
                axisLine: { show: false },
                axisTick: { show: false },
                axisLabel: {
                    margin: 45,
                    fontSize: 14,
                    align:'left',
                    // position:'left',
                    textStyle: {
                    color: 'rgba(255,255,255,0.8)' // 设置图例的字体颜色为红色
                    }
                },
                axisPointer: {
                    label: {
                    show: true,
                    margin: 30,
                    // position:'right',
                    },
                }
                },
                xAxis: {
                splitLine: { show: false },
                axisLabel: { show: false },
                axisTick: { show: false },
                axisLine: { show: false }
                },
                animationDurationUpdate: 500,
                series: [
                {
                    name: '2022',
                    id: 'bar1',
                    type: type,
                    label: labelSetting,
                    symbolRepeat: true,
                    symbolSize: ['120%', '100%'],
                    barCategoryGap: '20%',
                    universalTransition: {
                    enabled: true,
                    delay: function (idx, total) {
                        return (idx / total) * 1000;
                    }
                    },
                    itemStyle:{
                        normal:{
                            color:"rgb(158,32,32)",
                        },
                    },
                    zlevel:11,
                    data: [
                    {
                        value: 21,
                        symbol: symbol || pathSymbols.bad
                    },
                    {
                        value: 66,
                        symbol: symbol || pathSymbols.low
                    },
                    {
                        value: 78,
                        symbol: symbol || pathSymbols.mid
                    },
                    {
                        value: 123,
                        symbol: symbol || pathSymbols.high
                    }
                    ]
                },
                {
                    name: '2023',
                    id: 'bar2',
                    type: type,
                    barGap: '20%',
                    label: labelSetting,
                    symbolRepeat: true,
                    symbolSize: ['120%', '100%'],
                    universalTransition: {
                    enabled: true,
                    delay: function (idx, total) {
                        return (idx / total) * 1000;
                    }
                    },
                    itemStyle:{
                        normal:{
                            color:"rgb(244,190,56)",
                        },
                    },
                    zlevel:11,
                    data: [
                    {
                        value: 29,
                        symbol: symbol || pathSymbols.bad
                    },
                    {
                        value: 73,
                        symbol: symbol || pathSymbols.low
                    },
                    {
                        value: 91,
                        symbol: symbol || pathSymbols.mid
                    },
                    {
                        value: 95,
                        symbol: symbol || pathSymbols.high
