                //     itemStyle: {
                //         normal: {
                //             color: "black",
                //         },
                //     },
                // }
                ]
            };
            }
            const options = [makeOption('pictorialBar'), makeOption('bar')];
            const optionIndex = ref(0);
            const option = options[optionIndex.value];
            setInterval(function () {
            optionIndex.value = (optionIndex.value + 1) % options.length;
            chartInstance.value.setOption(options[optionIndex.value]);
            }, 2500);
                        
            chartInstance.value.setOption(option);
}

        // 更新图表配置项
    function updateChart() {
            // 处理数据
            const valueData = allData.value[currentDataIndex.value].children;
            const legenDateArr = valueData.map(item => item.name);
            const seriesDataArr = valueData.map(item => {
                return {
                    name: item.name,
                    value: item.value,
                    children: item.children,
                };
            });
            const dataOption = {
                legend: {
                    data: legenDateArr,
                },
                series: [
                    {
                        data: seriesDataArr,
                    },
                ],
            };
            chartInstance.value.setOption(dataOption);
        }
    function removeChart() {
            chartInstance.value.dispose();
            chartInstance.value = null;
        }
    function toggleDropdown() {
            showDropdown.value = !showDropdown.value;
        }
    function switchData(index) {
            currentDataIndex.value = index;
            tit.value = allData.value[currentDataIndex.value].name;
            showDropdown.value = false;
            getData();
        }

